let toggles,swipeSection,swiper,BGM,song=!1,shoot_fx,shoot_status=!1,initToggles=()=>{toggles.forEach(t=>{t.addEventListener("click",()=>{t.matches(".on")?(animation_toggle_off(t),animation_toggles.finished.then(()=>{checking_bgm(t),checking_shoot(t),t.classList.remove("on")})):(animation_toggle_on(t),animation_toggles.finished.then(()=>{checking_bgm(t),checking_shoot(t),t.classList.add("on")}))})})},checking_bgm=t=>{t.matches(".BGM")&&(song?(BGM.pause(),song=!1):(BGM.play(),song=!0))},checking_shoot=t=>{t.matches(".shoots")&&(shoot_status?(shoot_fx.pause(),shoot_status=!1):(shoot_fx.play(),shoot_status=!0))},initSwiper=()=>{swiper=new Swiper(swipeSection,{direction:"horizontal",spaceBetween:30,loop:!1,initialSlide:1,pagination:{el:"swiper_item"}})};const initUI=()=>{BGM=document.getElementById("bgm"),shoot_fx=document.getElementById("shoot"),toggles=GAME_UI.app.querySelectorAll(".toggle"),initToggles(),swipeSection=".swiper_section",initSwiper()};